
def fator(n_, param_):

    cont_ = param_[0]; p = param_[1] 
        
    a = param_[3]; b = param_[2]; c= param_[2]

 
    while cont_ < n_-1:
   
        c = (b+a)%(10**9+7) 
        a = b
        b = c

        cont_ += 1
        p = (p*2)%(10**9+7)
        


    c = (2*c - 1)%(10**9+7) 
    r_ = (p*(2*c-(n_+1)))

    
    return r_


